CREATE TABLE `ims_user_signin` ( `uid` int(10) unsigned NOT NULL DEFAULT '0', `count` tinyint(2) unsigned DEFAULT '0' COMMENT '连续签到次数', `lasttime` int(10) unsigned DEFAULT '0' ) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4;
CREATE TABLE `dg_user_signin_log` ( `id` int(11) NOT NULL, `uid` int(10) unsigned DEFAULT '0', `score` smallint(5) unsigned DEFAULT '0', `intro` varchar(100) DEFAULT '', `create_time` int(10) unsigned DEFAULT '0', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COMMENT='积分消耗表';
CREATE TABLE `dg_user_signin_rule` ( `rule` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '规则天数 对应dg_user_signin的count', `score` tinyint(2) unsigned DEFAULT '0' COMMENT '增加积分' ) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4;
CREATE TABLE `dk_address` ( `uid` int(11) NOT NULL, `address` varchar(200) DEFAULT '', `create_time` int(11) DEFAULT NULL, `update_time` int(11) DEFAULT NULL ) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4;
CREATE TABLE `dk_money` ( `uid` int(11) NOT NULL, `money` decimal(12,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '余额', PRIMARY KEY (`uid`,`money`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COMMENT='总充值余额';
CREATE TABLE `dk_money_sign` ( `id` int(10) unsigned NOT NULL, `uid` int(11) DEFAULT NULL, `money` decimal(12,2) unsigned DEFAULT '0.00', `day_count` smallint(5) unsigned DEFAULT '0', `start_date` date DEFAULT '0000-00-00', `create_time` int(10) unsigned DEFAULT '0', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COMMENT='总充值余额';
CREATE TABLE `dk_money_sign_log` ( `id` int(11) NOT NULL, `uid` int(10) unsigned DEFAULT '0', `score` smallint(5) unsigned DEFAULT '0', `intro` varchar(100) DEFAULT '', `create_time` int(10) unsigned DEFAULT '0', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COMMENT='积分消耗表';
CREATE TABLE `dk_money_sign_order` ( `id` int(11) NOT NULL, `uid` int(10) unsigned DEFAULT '0', `money` int(11) DEFAULT NULL, `trade_no` varchar(50) DEFAULT '' COMMENT '微信返回的交易号', `status` tinyint(1) unsigned DEFAULT '1', `create_time` int(10) unsigned DEFAULT '0', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COMMENT='充值记录';
CREATE TABLE `dk_money_sign_record` ( `id` int(10) unsigned NOT NULL, `uid` int(11) unsigned DEFAULT '0', `money` decimal(12,2) unsigned DEFAULT '0.00', `day_count` smallint(5) unsigned DEFAULT '0', `start_date` date DEFAULT '0000-00-00', `create_time` int(10) unsigned DEFAULT '0', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COMMENT='总充值余额';
CREATE TABLE `dk_product` ( `id` int(11) NOT NULL, `name` varchar(30) DEFAULT NULL, `money` decimal(12,2) DEFAULT NULL, `score` smallint(5) unsigned DEFAULT '0', `intro` text, `status` tinyint(1) DEFAULT '1' COMMENT '1上架 0下架 -1删除', `create_time` int(11) DEFAULT NULL, `update_time` int(11) DEFAULT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4;
CREATE TABLE `dk_product_order` ( `id` int(11) NOT NULL, `uid` int(10) unsigned DEFAULT '0', `aid` int(11) unsigned DEFAULT '0' COMMENT 'address的id', `pid` int(50) unsigned DEFAULT '0' COMMENT '商品id', `trade_no` varchar(50) DEFAULT '', `status` tinyint(1) unsigned DEFAULT '1', `create_time` int(10) unsigned DEFAULT '0', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4;
CREATE TABLE `dk_product_pj` ( `pid` int(11) NOT NULL, `uid` int(10) unsigned NOT NULL DEFAULT '0', `star` tinyint(1) unsigned DEFAULT '0' COMMENT 'address的id', `msg` varchar(50) DEFAULT '0' COMMENT '商品id', `status` tinyint(1) unsigned DEFAULT '1', `create_time` int(10) unsigned DEFAULT '0', PRIMARY KEY (`pid`,`uid`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4;
CREATE TABLE `dk_score` ( `uid` int(10) unsigned NOT NULL DEFAULT '0', `score` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '总积分', PRIMARY KEY (`uid`,`score`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4;
CREATE TABLE `dk_socre_consume` ( `id` int(11) NOT NULL, `uid` int(10) unsigned DEFAULT '0', `score` smallint(5) unsigned DEFAULT '0', `intro` varchar(100) DEFAULT '', `create_time` int(10) unsigned DEFAULT '0', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COMMENT='积分消耗表';
CREATE TABLE `dk_socre_log` ( `id` int(11) NOT NULL, `uid` int(10) unsigned DEFAULT '0', `type` tinyint(1) unsigned DEFAULT '1' COMMENT '1签到 2任务', `param` int(10) unsigned DEFAULT '0' COMMENT 'type=1 空 =2 任务id', `score` smallint(5) unsigned DEFAULT '0', `intro` varchar(100) DEFAULT '', `create_time` int(10) unsigned DEFAULT '0', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COMMENT='积分消耗表';
CREATE TABLE `dk_user` ( `uid` int(11) NOT NULL, `nickname` varchar(12) DEFAULT '', `sex` tinyint(1) DEFAULT NULL, `birth_month` tinyint(2) unsigned DEFAULT '0', `birth_day` tinyint(2) unsigned DEFAULT '0', `headpic` varchar(60) DEFAULT '', `email` varchar(30) DEFAULT '', `phone` varchar(11) DEFAULT '', `openid` varchar(50) DEFAULT '', `wechat` varchar(20) DEFAULT '', PRIMARY KEY (`uid`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4;
